import React from "react";
import logo from "./refresh_btn.png";
import { useState } from "react";

export function Counter() {
  const [count, setCount] = useState(0);
  const [isEven, setIsEven] = useState("НАЖИМАЙ");

  const isEvenMessage = (count) => {
    if (count % 2 === 0) {
      setIsEven("Чётное число");
    } else {
      setIsEven("Нечётное число");
    }
  };

  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          color: "lightgray",
          fontWeight: "semi-bold",
          border: "1px solid black",
          width: "300px",
          height: "400px",
          margin: "30px",
        }}
      >
        <p
          style={{
            fontSize: 100,
          }}
          onChange={isEvenMessage()}
        >
          {count}
        </p>
        <p
          style={{
            fontSize: 100,
          }}
        >
          {isEven}
        </p>

        <div
          style={{
            display: "flex",
            flexDirection: "row",
            width: "300px",
            height: "300px",
            color: "white",
          }}
        >
          {
            <button
              style={{
                fontSize: 50,
                width: "100px",
                backgroundColor: "red",
              }}
              onClick={() => setCount(count - 1)}
            >
              -
            </button>
          }
          {
            <button
              style={{
                fontSize: 50,
                width: "100px",
                alignItems: "center",
                justifyContent: "center",
                backgroundColor: "orange",
              }}
              onClick={() => setCount(0)}
            >
              <img
                src={logo}
                alt="logo"
                style={{
                  width: "50px",
                  height: "50px",
                }}
              ></img>
            </button>
          }

          {
            <button
              style={{
                fontSize: 50,
                width: "100px",
                backgroundColor: "green",
              }}
              onClick={() => setCount(count + 1)}
            >
              +
            </button>
          }
        </div>
      </div>
    </div>
  );
}
