import logo from "./refresh_btn.png";
import { useState, useEffect } from "react";

export function Counter() {
  const [count, setCount] = useState(0);
  const [isEven, setIsEven] = useState("НАЖИМАЙ");
  const [bgColor, setBgColor] = useState("yellow");

  const isEvenMessage = () => {
    if (count % 2 === 0 && count !== 0) {
      return setIsEven("Чётное число"), setBgColor("blue");
    } else if (count === 0) {
      return setIsEven("Нолик"), setBgColor("yellow");
    } else {
      return setIsEven("Нечётное число"), setBgColor("green");
    }
  };

  const counterAboveZero = () => {
    if (button.className === "minus" && count >= 0) {
      return setCount(count - 1);
    } else if (button.className === "plus" && count >= 0) {
      return setCount(count + 1);
    } else return setIsEven("Ниже нуля счётчик не работает");
  };
  console.log(count);
  console.log(isEven);

  useEffect(() => {
    isEvenMessage();
  });

  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          color: "lightgray",
          fontWeight: "semi-bold",
          border: "1px solid black",
          width: "300px",
          height: "300px",
          margin: "30px",
          backgroundColor: bgColor,
        }}
      >
        <p
          style={{
            fontSize: 100,
          }}
          onChange={() => isEvenMessage(count)}
        >
          {count}
        </p>
        <p
          style={{
            fontSize: 30,
          }}
        >
          {isEven}
        </p>
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            width: "300px",
            height: "300px",
            color: "white",
          }}
        >
          {
            <button
              className="minus"
              style={{
                fontSize: 50,
                width: "100px",
                backgroundColor: "red",
              }}
              onClick={() => setCount(count - 1)}
            >
              -
            </button>
          }
          {
            <button
              style={{
                fontSize: 50,
                width: "100px",
                alignItems: "center",
                justifyContent: "center",
                backgroundColor: "orange",
              }}
              onClick={() => setCount(0)}
            >
              <img
                src={logo}
                alt="logo"
                style={{
                  width: "50px",
                  height: "50px",
                }}
              ></img>
            </button>
          }

          {
            <button
              className="plus"
              style={{
                fontSize: 50,
                width: "100px",
                backgroundColor: "green",
              }}
              onClick={() => setCount(count + 1)}
            >
              +
            </button>
          }
        </div>
      </div>
    </div>
  );
}
